import React, { useState } from "react";
import productlist from "../styles/productlist.css";

const ProductList = () => {
  const [product, setProduct] = useState({
    name: "",
    price: "",
    about: "",
  });
  const [file, setFile] = useState(null);

  // Handle text inputs
  const handleInputs = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  // Handle file input
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  // Submit form data
  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", product.name);
    formData.append("price", product.price);
    formData.append("about", product.about);
    if (file) {
      formData.append("file", file);
    }
    if (!file) {
      console.log("please incert Photo of Product!!");
    }
    try {
      const response = await fetch("http://localhost:4000/v1/imageUpload", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      if (data) {
        console.log("Product Listed Successfully!!");
      }
      console.log("Response from server:", data);
    } catch (error) {
      console.error("Error sending data to server:", error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          value={product.name}
          onChange={handleInputs}
          placeholder="Name of Product"
          name="name"
          id="name"
          type="text"
        />
        <input
          type="file"
          name="file"
          id="file"
          placeholder="Upload Photo"
          onChange={handleFileChange}
        />
        <input
          value={product.price}
          onChange={handleInputs}
          type="number"
          name="price"
          id="price"
          placeholder="Price"
        />
        <input
          value={product.about}
          onChange={handleInputs}
          type="text"
          name="about"
          id="about"
          placeholder="About Product"
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ProductList;
